
public class ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Declarando variables numericas con distinto valor.
		
		int a=10;
		int b=4;
		
		//Usamos Math Max y Min entre las variables para que nos indique cual de las dos es menor o mayor.
		
		System.out.println("Entre el valor a(10) y el valor b(4), el mayor es: "+Math.max(a, b));
		System.out.println("Entre el valor a(10) y el valor b (4), el menor es: "+Math.min(a, b));
		
	}
	
}

